import { ActivatedRoute, Router } from '@angular/router';
import { Component, OnDestroy, OnInit } from '@angular/core';
import { Subject } from 'rxjs';
import { Client } from '../Models/models';
import { FormControl } from '@angular/forms';
import { clientService } from '../Services/client.service';
import { MatDivider, MatDividerModule } from '@angular/material/divider';
import { MatFormField, MatFormFieldModule, MatLabel } from '@angular/material/form-field';
import { MatIcon, MatIconModule } from '@angular/material/icon';
import { MatPaginator, MatPaginatorModule } from '@angular/material/paginator';
import { MatProgressSpinner, MatProgressSpinnerModule } from '@angular/material/progress-spinner';
import { MatButtonModule } from '@angular/material/button';
import { MatExpansionPanelDescription, MatExpansionPanelHeader, MatExpansionPanelTitle } from '@angular/material/expansion';
import { MatDialogModule } from '@angular/material/dialog';
import { MatTableModule } from '@angular/material/table';

@Component({
  selector: 'app-client',
  standalone: true,
  imports:  [MatDividerModule,
    MatFormFieldModule,
    MatIconModule,
    MatPaginatorModule,
    MatProgressSpinnerModule,
    MatButtonModule,
    MatTableModule,
    MatDialogModule],
  templateUrl: './client.component.html',
  styleUrl: './client.component.scss'
})
export class ClientComponent implements OnInit,OnDestroy {

  update = new Subject<boolean>();

  dataSource: Client[] = [];

  displayedColumns = [
    //  'select',
    'name',
    'tel',
    'adresse',
    'option'
  ];

  panelOpenState = false;

  reference = new FormControl('');
  designation = new FormControl('');

  constructor(private Router: Router, private route: ActivatedRoute, private service: clientService) { }

  ngOnDestroy(): void {
  }

  ngOnInit(): void {
    const sub = this.service.get().subscribe(
      (r: any) => {
        console.log(r);
        this.dataSource = r;
      }

    );
    console.log("<<<<<<<<<<<<>>>>>>>>>>");

  }

  add() {

    this.Router.navigate(['/client/update', 0]);
    // this.router.navigate(['update', 0], { relativeTo: this.route });
  }

  edit(o: Client) {

    this.Router.navigate(['/client/update', o.id]);
    // this.router.navigate(['update', o.id], { relativeTo: this.route });

  }
 printD(){
window.print();
 }
  async delete(o: Client) {
    const r = await this.service.deleteDialog.openDialog('client').toPromise();
    if (r === 'ok') {
      const sub = this.service.delete(o.id).subscribe(() => this.update.next(true));

      // this.subs.push(sub);
    }
  }

}
<div class="flex flex-col gap-5">
  <h1 class="font-normal md:font-bold">Mise à jour des client</h1>
  <table mat-table [dataSource]="dataSource" class="w-full rounded-xl">
    <ng-container matColumnDef="name">
      <th mat-header-cell *matHeaderCellDef mat-sort-header class="p-3 text-sm font-sans tracking-wider text-left">Name
      </th>
      <td mat-cell *matCellDef="let row" class="p-4 text-sm font-sans text-gray-500">{{row.name}}</td>
    </ng-container>

    <ng-container matColumnDef="tel">
      <th mat-header-cell *matHeaderCellDef mat-sort-header class="p-3 text-sm font-sans tracking-wider text-left">Tel
      </th>
      <td mat-cell *matCellDef="let row" class="p-4 text-sm font-sans text-gray-500">{{row.tel}}</td>
    </ng-container>

    <ng-container matColumnDef="adresse">
      <th mat-header-cell *matHeaderCellDef mat-sort-header class="p-3 text-sm font-sans tracking-wider text-left">
        Adresse</th>
      <td mat-cell *matCellDef="let row" class="p-4 text-sm font-sans text-gray-500">{{row.adresse}}</td>
    </ng-container>

    <ng-container matColumnDef="option" style="flex-direction: row-reverse;">
      <th mat-header-cell *matHeaderCellDef>
        <button mat-icon-button color="accent" (click)="add()">
          <mat-icon>add</mat-icon>
        </button>
      </th>
      <td mat-cell *matCellDef="let row">
        <div class="button-row">
          <button mat-icon-button color="accent" (click)="edit(row)">
            <mat-icon>create</mat-icon>
          </button>

          <button mat-icon-button color="primary" (click)="delete(row)">
            <mat-icon>delete_sweep</mat-icon>
          </button>
          <button mat-icon-button color="primary" (click)="printD()">
            <mat-icon>print</mat-icon>
          </button>
        </div>
      </td>
    </ng-container>
    <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
    <tr mat-row *matRowDef="let row; columns: displayedColumns;"></tr>
  </table>
</div>
