import { Component, OnDestroy, OnInit, ViewChild, inject } from '@angular/core';
import { FormControl, FormGroup } from '@angular/forms';
import { MatDivider, MatDividerModule } from '@angular/material/divider';
import { MatExpansionPanelDescription, MatExpansionPanelHeader, MatExpansionPanelTitle } from '@angular/material/expansion';
import { MatFormField, MatFormFieldModule, MatLabel } from '@angular/material/form-field';
import { MatIcon, MatIconModule } from '@angular/material/icon';
import { MatPaginator, MatPaginatorModule } from '@angular/material/paginator';
import { MatProgressSpinner, MatProgressSpinnerModule } from '@angular/material/progress-spinner';
import { MatSort } from '@angular/material/sort';
import { Subject, Subscription, merge, startWith } from 'rxjs';
import { Article } from '../Models/models';
import { ActivatedRoute, Router } from '@angular/router';
import { MatTableModule } from '@angular/material/table';
import { ArticleService } from '../Services/article.service';
import { __await } from 'tslib';
import { MatDialogModule } from '@angular/material/dialog';
import { MatButtonModule } from '@angular/material/button';
import { MatCardModule } from '@angular/material/card';

@Component({
  selector: 'app-article',
  standalone: true,
  imports: [MatDividerModule,
    MatFormFieldModule,
    MatCardModule,
    MatPaginatorModule,
    MatProgressSpinnerModule,
    MatButtonModule,
    MatTableModule,
    MatDialogModule,
    MatIconModule],
  templateUrl: './article.component.html',
  styleUrl: './article.component.scss'
})
export class ArticleComponent implements OnInit, OnDestroy {
  // @ViewChild(MatPaginator) paginator: MatPaginator;
  // @ViewChild(MatSort) sort: MatSort;

  update = new Subject<boolean>();

  dataSource: Article[] = [];

  displayedColumns = [
    //  'select',
    'reference',
    'designation',
    'stockInitial',
    'stockFinal',
    'qteAchete',
    'qteVendue',
    'prixAchat_HT',
    'prixAchat_TTC',
    'prixVente_HT',
    'prixVente_TTC',
    'info',
    'option'
  ];

  panelOpenState = false;

  reference = new FormControl('');
  designation = new FormControl('');

  constructor(private router: Router, private route: ActivatedRoute, private service: ArticleService) { }

  ngOnDestroy(): void {
  }

  ngOnInit(): void {
    const sub = this.service.get().subscribe(
      (r: any) => {
        console.log(r);
        this.dataSource = r;
      }

    );
    console.log("<<<<<<<<<<<<>>>>>>>>>>");

  }

  add() {

    this.router.navigate(['/article/update', 0]);
    // this.router.navigate(['update', 0], { relativeTo: this.route });
  }

  edit(o: Article) {

    this.router.navigate(['/article/update', o.id]);
    // this.router.navigate(['update', o.id], { relativeTo: this.route });

  }
 printD(){
window.print();
 }
  async delete(o: Article) {
    const r = await this.service.deleteDialog.openDialog('Article').toPromise();
    if (r === 'ok') {
      const sub = this.service.delete(o.id).subscribe(() => this.update.next(true));

      // this.subs.push(sub);
    }
  }

}

<div class="flex flex-col gap-5"  >
    <h1 class="font-normal md:font-bold">Mise à jour des articles</h1>
     <table mat-table [dataSource]="dataSource">
      <ng-container matColumnDef="reference" >
        <th mat-header-cell *matHeaderCellDef mat-sort-header class="p-3 text-sm font-sans tracking-wider text-left">Référence</th>
        <td mat-cell *matCellDef="let row" class="p-4 text-sm font-sans text-gray-500">{{row.reference}}</td>
      </ng-container>

      <ng-container matColumnDef="designation">
        <th mat-header-cell *matHeaderCellDef mat-sort-header class="p-3 text-sm font-sans tracking-wider text-left">Désignation</th>
        <td mat-cell *matCellDef="let row" class="p-4 text-sm font-sans text-gray-500">{{row.designation}}</td>
      </ng-container>

      <ng-container matColumnDef="stockInitial">
        <th mat-header-cell *matHeaderCellDef mat-sort-header class="p-3 text-sm font-sans tracking-wider text-left">Stock Initial</th>
        <td mat-cell *matCellDef="let row" class="p-4 text-sm font-sans text-gray-500">{{row.stockInitial}}</td>
      </ng-container>

      <ng-container matColumnDef="stockFinal">
        <th mat-header-cell *matHeaderCellDef mat-sort-header class="p-3 text-sm font-sans tracking-wider text-left">Stock Final</th>
        <td mat-cell *matCellDef="let row" class="p-4 text-sm font-sans text-gray-500">{{row.stockFinal}}</td>
      </ng-container>

      <ng-container matColumnDef="qteAchete">
        <th mat-header-cell *matHeaderCellDef mat-sort-header class="p-3 text-sm font-sans tracking-wider text-left">Qté Entrée</th>
        <td mat-cell *matCellDef="let row" class="p-4 text-sm font-sans text-gray-500">{{row.qteAchete}}</td>
      </ng-container>

      <ng-container matColumnDef="qteVendue">
        <th mat-header-cell *matHeaderCellDef mat-sort-header class="p-3 text-sm font-sans tracking-wider text-left">Qté Sortie</th>
        <td mat-cell *matCellDef="let row" class="p-4 text-sm font-sans text-gray-500">{{row.qteVendue}}</td>
      </ng-container>

      <ng-container matColumnDef="prixAchat_HT">
        <th mat-header-cell *matHeaderCellDef mat-sort-header class="p-3 text-sm font-sans tracking-wider text-left">PUA HT</th>
        <td mat-cell *matCellDef="let row" class="p-4 text-sm font-sans text-gray-500">{{row.prixAchat_HT}}</td>
      </ng-container>

      <ng-container matColumnDef="prixAchat_TTC">
        <th mat-header-cell *matHeaderCellDef mat-sort-header class="p-3 text-sm font-sans tracking-wider text-left">PUA TTC</th>
        <td mat-cell *matCellDef="let row" class="p-4 text-sm font-sans text-gray-500">{{row.prixAchat_TTC}}</td>
      </ng-container>

      <ng-container matColumnDef="prixVente_HT">
        <th mat-header-cell *matHeaderCellDef mat-sort-header class="p-3 text-sm font-sans tracking-wider text-left">Puv HT</th>
        <td mat-cell *matCellDef="let row" class="p-4 text-sm font-sans text-gray-500">{{row.prixVente_HT}}</td>
      </ng-container>

      <ng-container matColumnDef="prixVente_TTC">
        <th mat-header-cell *matHeaderCellDef mat-sort-header class="p-3 text-sm font-sans tracking-wider text-left">PUV TTC</th>
        <td mat-cell *matCellDef="let row" class="p-4 text-sm font-sans text-gray-500">{{row.prixVente_TTC}}</td>
      </ng-container>

      <ng-container matColumnDef="info">
        <th mat-header-cell *matHeaderCellDef mat-sort-header class="p-3 text-sm font-sans tracking-wider text-left">Info</th>
        <td mat-cell *matCellDef="let row" class="p-4 text-sm font-sans text-gray-500">{{row.info}}</td>
      </ng-container>

      <ng-container matColumnDef="option" style="flex-direction: row-reverse;">
        <th mat-header-cell *matHeaderCellDef >
          <button mat-icon-button color="primary" (click)="add()">
            <mat-icon svgIcon="add_circle"></mat-icon>
          </button>
        </th>
        <td mat-cell *matCellDef="let row">
          <div class="button-row">
            <button mat-icon-button  color="primary" (click)="edit(row)">
                <mat-icon svgIcon="edit"></mat-icon>
            </button>
            <button mat-icon-button color="warn" (click)="delete(row)">
                <mat-icon svgIcon="delete"></mat-icon>
            </button>
            <button mat-icon-button color="primary" (click)="printD()">
                <mat-icon svgIcon="print"></mat-icon>
            </button>
          </div>
        </td>
      </ng-container>
      <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
      <tr mat-row *matRowDef="let row; columns: displayedColumns;"></tr>
    </table>
  </div>
